		return 1;

	if (distortionCorrected == 1)
		for (int ii = 0; ii < nCams; ii++)
			for (int jj = 0; jj < 7; jj++)
				camera[ii].distortion[jj] = 0.0;

	for (int ii = 0; ii < nCams; ii++)
		camera[ii].threshold = 3.0, camera[ii].ninlierThresh = 50;

	char Fname[200];
